INSERT INTO "livros" VALUES(1,'Amor de cabelo','Matthew A. Cherry','Galerinha','Afroestima','a','Infantil (4-8 anos)','Zuri ama seu cabelo porque ele é cheio de possibilidades. Quando precisa de um penteado especial para receber a mãe, seu pai entra em ação e, juntos, eles celebram identidade, cuidado e amor-próprio.',32,'2020','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AmorCabelo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AmorCabelo.jpeg?raw=true');
INSERT INTO "livros" VALUES(2,'Ananda Por que sou tão pretinha?','Henrique André','Kitembo','Afroestima','a','Infantil','Ananda pergunta por que é tão pretinha e, com ajuda da família, mergulha em histórias de reis e rainhas africanos, ressignificando sua identidade e celebrando sua cor.',28,'16 de novembro de 2023','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TaoPretinha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TaoPretinha.jpeg?raw=true');
INSERT INTO "livros" VALUES(3,'Com qual penteado eu vou?','Kiusam de Oliveira','Melhoramentos','a','Afroestima','Leitor em processo fluente (aprox. 8+)','Na festa de 100 anos do bisavô Seu Benedito, as crianças escolhem penteados e oferecem virtudes como presentes, valorizando herança afro-brasileira, nomes e identidade.',48,'15 de junho de 2021','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Conceicao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Conceicao.jpeg?raw=true');
INSERT INTO "livros" VALUES(4,'Dadá e o Garfo Mágico','Flávia Pimenta','Kitembo','Afroestima','a','Infantil','Informações insuficientes nos catálogos consultados. Inserir sinopse quando disponível.',26,'21 de julho de 2023','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Garfo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Garfo.jpeg?raw=true');
INSERT INTO "livros" VALUES(5,'Meu crespo é de rainha','bell hooks','Boitatá','Afroestima','a','Infantil','Poema rimado ilustrado que celebra os cabelos crespos e cacheados como coroas de identidade e poder, convidando meninas negras a se reconhecerem e se orgulharem desde a infância.',32,'8 de março de 2018','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Conceicao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Conceicao.jpeg?raw=true');
INSERT INTO "livros" VALUES(6,'Luanda no mundo da ciência','Ana Lúcia Nunes de Sousa','Kitembo','Empoderamento Feminino','a','Infantil','Luanda é uma menina curiosa e inteligente que nos leva a descobrir o mundo da ciência e o poder do conhecimento.',32,'2023-01-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LuandaNoMundoDaCiencia.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LuandaNoMundoDaCiencia.jpeg?raw=true');
INSERT INTO "livros" VALUES(7,'101 Mulheres Incríveis Que Mudaram o Mundo','Julia Adams','Pé da Letra','Empoderamento feminino','a','Infantojuvenil','Coletânea de breves biografias de 101 mulheres que marcaram a história em diversas áreas, com linguagem envolvente e ilustrações coloridas.',128,'2021','2025-07-25','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/101.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/101.jpeg?raw=true');
INSERT INTO "livros" VALUES(8,'Sejamos Todos Feministas (versão adaptada para jovens)','Chimamanda Ngozi Adichie','Companhia das Letrinhas','Empoderamento Feminino','a','Infantojuvenil','Uma introdução poderosa ao feminismo, adaptada especialmente para o público jovem, escrita por uma das maiores vozes contemporâneas.',64,'2017-11-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/SejamosTodosFeministas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/SejamosTodosFeministas.jpeg?raw=true');
INSERT INTO "livros" VALUES(9,'50 brasileiras incríveis para conhecer antes de crescer','Débora Thomé','Galera (Grupo Record)','empoderamento feminino','a','Infantojuvenil','Biografias curtas de brasileiras que marcaram a história em diversas áreas como política, ciência, arte e esportes. Um livro inspirador para jovens leitoras.',120,'2018-02-28','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/50Brasileiras.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/50Brasileiras.jpeg?raw=true');
INSERT INTO "livros" VALUES(10,'A princesa que salvava príncipes','Claudia Souza','Callis','empoderamento feminino','a','Infantil','Subvertendo o clássico conto de fadas, essa história apresenta uma princesa forte e independente que enfrenta seus próprios desafios e salva príncipes.',32,'2012-05-18','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/APrincesaQueSalvaPrincipe.jpg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/APrincesaQueSalvaPrincipe.jpeg?raw=true');
INSERT INTO "livros" VALUES(11,'50 personalidades negras revolucionárias','Karina Barbosa dos Santos','Ciranda Cultural','empoderamento feminino','a','Infantojuvenil','Apresenta 50 biografias de personalidades negras que marcaram e continuam marcando o mundo — com histórias de resistência, coragem e contribuição social.',112,'2023-09-10','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/50PersonalidadesNegras.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/50PersonalidadesNegras.jpeg?raw=true');
INSERT INTO "livros" VALUES(12,'Ada Batista, Cientista','Andrea Beaty','Intrínseca','empoderamento feminino','a','Infantil','Ada é uma menina curiosa que só começou a falar aos três anos. Com sede de saber, ela questiona o mundo ao seu redor e empreende experimentos engraçados e instigantes, revelando seu espírito de cientista desde cedo.',32,'2019-10-07','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AdaBatistaCientista.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AdaBatistaCientista.jpeg?raw=true');
INSERT INTO "livros" VALUES(13,'Cinderela e o baile dela','Janaina Tokitaka','Companhia das Letrinhas','empoderamento feminino','a','Infantil','Reconto irreverente da história de Cinderela, com princesas negras e princípios de autonomia e protagonismo feminino.',32,'2023-09-01','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CinderelaBaile.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CinderelaBaile.jpeg?raw=true');
INSERT INTO "livros" VALUES(14,'Coisa de menina','Pri Ferrari','Companhia das Letrinhas','Empoderamento Feminino','a','Infantil','Mostra profissões e atividades fora dos estereótipos de gênero, incentivando meninas a se verem em múltiplas possibilidades.',48,'2016-10-04','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CoisaDeMenina.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CoisaDeMenina.jpeg?raw=true');
INSERT INTO "livros" VALUES(15,'Contos de Fadas para Garotas Corajosas','Anita Ganeri','Pé da Letra','Empoderamento Feminino','a','Infantojuvenil','Recontos de quinze contos populares protagonizados por mulheres corajosas que traçam o próprio caminho e desafiam estereótipos.',128,'2021-02-10','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ContosFadasGarotasCorajosas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ContosFadasGarotasCorajosas.jpeg?raw=true');
INSERT INTO "livros" VALUES(16,'Grandes mulheres que mudaram o mundo','Kate Pankhurst','V&R Editoras','Empoderamento Feminino','a','Infantojuvenil','Apresentação de mulheres que fizeram história em diversas áreas, incentivando meninas a acreditarem em seus sonhos e serem protagonistas.',32,'2018-01-01','2025-07-27','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/GrandesMulheresMudaramOMundo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/GrandesMulheresMudaramOMundo.jpeg?raw=true');
INSERT INTO "livros" VALUES(17,'Harriet Tubman','Majori Silva','Mostarda','Empoderamento Feminino','a','Infantojuvenil','Biografia em quadrinhos que narra a trajetória de Harriet Tubman, uma guerreira da liberdade que ajudou centenas de pessoas a escapar da escravidão.',64,'2023-09-11','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/HarrietTubman.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/HarrietTubman.jpeg?raw=true');
INSERT INTO "livros" VALUES(18,'Meninas Sonhadoras, Mulheres Cientistas – Linguagens e Ciências Humanas','Flávia Martins de Carvalho','Mostarda','Empoderamento Feminino','a','Infantojuvenil','Inspira meninas a seguirem carreiras nas ciências humanas, mostrando a trajetória de grandes mulheres da área.',40,'2022-10-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninasSonhadorasCienciasHumanas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninasSonhadorasCienciasHumanas.jpeg?raw=true');
INSERT INTO "livros" VALUES(19,'Mulheres Incríveis que Mudaram o Mundo','Alice Ramos','Ciranda Cultural','Empoderamento Feminino','a','Infantil','Apresenta a vida de mulheres notáveis que impactaram a história, incentivando novas gerações com suas conquistas.',28,'2021-03-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MulheresIncriveis.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MulheresIncriveis.jpeg?raw=true');
INSERT INTO "livros" VALUES(20,'Meninas Sonhadoras, Mulheres Cientistas – Sustentabilidade I','Flávia Martins de Carvalho','Mostarda','Empoderamento Feminino','Ciência / Sustentabilidade','Infantojuvenil','Retrata o protagonismo feminino em pesquisas ligadas à sustentabilidade, destacando cientistas que atuam pela preservação do planeta.',42,'2023-05-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninasSonhadorasSustentabilidade.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninasSonhadorasSustentabilidade.jpeg?raw=true');
INSERT INTO "livros" VALUES(21,'Menina que Brincava de Tudo','Carolina Magalhães','Carolina Magalhães','Empoderamento Feminino','Ficção Infantil','Infantil','Uma história sobre liberdade, criatividade e empoderamento, mostrando que meninas podem brincar e ser o que quiserem.',24,'2021-07-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninaQueBrincavaDeTudo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninaQueBrincavaDeTudo.jpeg?raw=true');
INSERT INTO "livros" VALUES(22,'Tarsila, Menina Pintora','Lúcia Fidalgo','Paulus','Empoderamento Feminino','Biografia / Arte','Infantil','A trajetória da artista Tarsila do Amaral desde a infância, incentivando meninas a valorizarem a arte e sua identidade.',32,'2020-02-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TarsilaMeninaPintora.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TarsilaMeninaPintora.jpeg?raw=true');
INSERT INTO "livros" VALUES(23,'Pequenas Grandes Líderes: Mulheres Importantes da História Negra','Vashti Harrison','Harperkids','Empoderamento Feminino','Biografia / História Negra','Infantojuvenil','Retrata mulheres negras que marcaram a história com coragem, talento e liderança, inspirando novas gerações.',96,'2019-09-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PequenasGrandesLideres.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PequenasGrandesLideres.jpeg?raw=true');
INSERT INTO "livros" VALUES(24,'O Amor é Animal','José Roberto Torero','Padaria de Livros','Famílias Diversas','Infantil','Infantil','Uma história divertida e sensível que mostra como o amor pode existir de diferentes formas e em diferentes tipos de famílias.',32,'2018-05-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OAmorEAnimal.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OAmorEAnimal.jpeg?raw=true');
INSERT INTO "livros" VALUES(25,'Família','Núria Roca','Ibep JR','Famílias Diversas','Infantil','Infantil','Este livro mostra, de forma simples e inclusiva, os diferentes tipos de famílias existentes, promovendo respeito e empatia.',28,'2017-08-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Familia.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Familia.jpeg?raw=true');
INSERT INTO "livros" VALUES(26,'Mãe Não é Uma Só, Eu Tenho Duas','Nanda Matheus e Raphaela Comisso','Saíra','Famílias Diversas','Infantil','Infantil','Uma obra que apresenta com delicadeza a vivência de uma criança com duas mães, valorizando a diversidade e o amor familiar.',32,'2021-03-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MaeNaoEUmaSoEuTenhoDuas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MaeNaoEUmaSoEuTenhoDuas.jpeg?raw=true');
INSERT INTO "livros" VALUES(27,'Pequeno Sereio','Janaína Tokitaka','Companhia das Letrinhas','Famílias Diversas','Infantil','Infantil','Uma releitura moderna e inclusiva do clássico, em que o pequeno sereio vive entre mundos e identidades, abordando a diversidade familiar e de gênero.',28,'2020-10-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PequenoSereio.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PequenoSereio.jpeg?raw=true');
INSERT INTO "livros" VALUES(28,'Minhas Duas Avós','Ana Teixeira','Pólen','Famílias Diversas','Infantil','Infantil','Uma narrativa carinhosa sobre o vínculo com duas avós de culturas diferentes, valorizando a diversidade familiar e afetiva.',24,'2018-04-08','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MinhasDuasAvos.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MinhasDuasAvos.jpeg?raw=true');
INSERT INTO "livros" VALUES(29,'Olívia Tem Dois Pais','Márcia Leite','Companhia das Letrinhas','Famílias Diversas','Infantil','Infantil','A história de Olívia, uma menina com dois pais, que lida com curiosidades e desafios de maneira afetuosa e divertida.',30,'2021-01-22','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OliviaTemDoisPais.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OliviaTemDoisPais.jpeg?raw=true');
INSERT INTO "livros" VALUES(30,'Letras de Carvão','Irene Vasco & Juan Palomino','Pulo do Gato','Infanto-Juvenil','Narrativa ilustrada','Infantojuvenil','Na pequena cidade de Palenque, uma menina aprende a ler com letras escritas no chão com carvão, valorizando cultura quilombola e tradição oral.',36,'2016-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LetrasDeCarvao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LetrasDeCarvao.jpeg?raw=true');
INSERT INTO "livros" VALUES(31,'Jardim de Marielle','Majori Silva','Mostarda','Infanto-Juvenil','Narrativa metafórica infantil','Infantojuvenil','Inspirado em Marielle Franco, o livro conta como seu coração se transforma em semente e dá vida a uma árvore que floresce espalhando seu legado.',16,'2022-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/JardimDeMarielle.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/JardimDeMarielle.jpeg?raw=true');
INSERT INTO "livros" VALUES(32,'Estrelas','Júlio Emílio Braz','Oficina Raquel','infanto‑juvenil','Poesia infantil','Infantojuvenil','Narrativa poética que acolhe os medos infantis usando a metáfora do céu e das estrelas, trazendo conforto e autoestima às crianças.',24,'2021-10-04','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Estrelas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Estrelas.jpeg?raw=true');
INSERT INTO "livros" VALUES(33,'Ei, você! Um livro sobre crescer com orgulho de ser negro','Dapo Adeola','Companhia das Letrinhas','Infanto‑Juvenil','Não ficção infantil','Infantojuvenil','Livro ilustrado que celebra a vida e o crescimento de crianças negras, promovendo autoestima, representatividade e orgulho desde a infância.',56,'2021-09-24','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EiVoce.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EiVoce.jpeg?raw=true');
INSERT INTO "livros" VALUES(34,'Gabriela, a princesa do Daomé','Marta Rodrigues','Mazza Edições','Infanto‑Juvenil','Narrativa infantil','Infantojuvenil','Narrado por Gabriela, princesa de Daomé, que valoriza ancestralidade africana, coragem e vivência familiar, com mensagens de resistência e autoestima.',24,'2022-01-01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/GabrielaPrincesaDaome.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/GabrielaPrincesaDaome.jpeg?raw=true');
INSERT INTO "livros" VALUES(35,'Férias fantásticas de Lili','Lívia Natália','Ciclo Contínuo','Infanto‑Juvenil','Poesia infantil','Infantojuvenil','Lili vive aventuras poéticas durante as férias, com referências aos Orixás, despertando imaginação, ancestralidade e identidade negra.',32,'2018-09-30','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/FeriasFantasticasLili.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/FeriasFantasticasLili.jpeg?raw=true');
INSERT INTO "livros" VALUES(36,'Dona da festa','Elisa Lucinda','Galerinha','Infanto‑Juvenil','Literatura infantil','Infantil','Personagem alto‑astral que celebra o otimismo e a alegria infantis, com poesia e adivinhações apresentadas de forma lúdica.',24,'2011-03-14','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DonaDaFesta.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DonaDaFesta.jpeg?raw=true');
INSERT INTO "livros" VALUES(37,'De passinho em passinho','Otávio Júnior','Companhia das Letrinhas','Infanto‑Juvenil','Cultura e dança infantil','Infantojuvenil','Versos e ilustrações que celebram o ‘passinho’, ritmo nascido nas comunidades cariocas, combinando dança, identidade e expressão cultural.',32,'2021-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DePassinhoEmPassinho.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DePassinhoEmPassinho.jpeg?raw=true');
INSERT INTO "livros" VALUES(38,'Menino coração de tambor','Nilma Lino Gomes','Mazza Edições','Infanto‑Juvenil','Narrativa infantil ilustrada','Infantojuvenil','O coração de um menino bate conforme os ritmos que ele escuta ainda na barriga da mãe, celebrando música, cultura e movimento afro‑brasileiro.',32,'2013-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninoCoracaoTambor.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninoCoracaoTambor.jpeg?raw=true');
INSERT INTO "livros" VALUES(39,'Dengos na moringa de Voinha','Ana Fátima','Brinque‑Book','Infanto‑Juvenil','Conto infantil com ancestralidade','Infantojuvenil','A menina expressa memórias e afetos por meio da moringa da avó, símbolo de ancestralidade e tradição familiar em narrativas cotidianas.',32,'2023-06-27','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DengosMoringaVoinha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DengosMoringaVoinha.jpeg?raw=true');
INSERT INTO "livros" VALUES(40,'As descobertas de Kito','Douglas Coelho','Letraria','Infanto‑Juvenil','Literatura infantil paradidático','Infantojuvenil','Kito é um menino negro curioso que aprende sobre países de língua hispânica e compartilha suas descobertas, promovendo curiosidade, leitura e representatividade.',24,'2021-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DescobertasKito.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DescobertasKito.jpeg?raw=true');
INSERT INTO "livros" VALUES(41,'Meninas negras','Madu Costa','Mazza Edições','Infanto‑Juvenil','Identidade afrodescendente infantil','Infantojuvenil','Poemas e textos curtos que valorizam a identidade e cultura de meninas negras, reforçando autoestima e ancestralidade.',24,'2011-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninasNegras.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninasNegras.jpeg?raw=true');
INSERT INTO "livros" VALUES(42,'Lendas de Dandara','Jarid Arraes','Editora de Cultura','Infanto‑Juvenil','Cordel narrativo / lendas','Infantojuvenil','Narrativas em forma de lendas sobre Dandara dos Palmares, valorizando ancestralidade e heroísmo feminino negro.',128,'2016-12-01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LendasDeDandara.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LendasDeDandara.jpeg?raw=true');
INSERT INTO "livros" VALUES(43,'A luz de Aisha','Aza Njeri & Luana Rodrigues','Rebuliço','Infanto‑Juvenil','Narrativa filosófica afro-brasileira','Infantojuvenil','A pequena Aisha vive uma perda e, com apoio da memória ancestral comunitária e da filosofia bantu, encontra luz e afeto coletivo para seguir em frente.',56,'2021-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LuzDeAisha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LuzDeAisha.jpeg?raw=true');
INSERT INTO "livros" VALUES(44,'Mar de Manu','Cidinha da Silva','Kuanza Produções','Infanto‑Juvenil','Narrativa poética infantil','Infantojuvenil','Manu, um menino que pesca estrelas em um universo simbólico de Níger, Mali e Burkina Faso, traz reflexão sobre ancestralidade, pertencimento e sonho.',32,'2021-11-19','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MarDeManu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MarDeManu.jpeg?raw=true');
INSERT INTO "livros" VALUES(45,'Makori, a pequena princesa','Marcos Cajé','Ereginga Educação','Infanto‑Juvenil','Narrativa infantil com ancestralidade','Infantojuvenil','Makori é descendente de princesa africana e aprende com a ancestralidade e o samba‑de‑roda sobre força, resistência e identidade cultural.',44,'2022-01-01','2025-07-28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MakoriPequenaPrincesa.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MakoriPequenaPrincesa.jpeg?raw=true');
INSERT INTO "livros" VALUES(46,'Makeba vai à escola','Ana Fátima','Cogito','Infanto‑Juvenil','Narrativa infantil de iniciação escolar','Infantojuvenil','Makeba, uma menina de 3 anos, encara seu primeiro dia de escola com apoio da família, superando medos e celebrando sua negritude e identidade.',28,'2019-01-01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MakebaVaiEscola.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MakebaVaiEscola.jpeg?raw=true');
INSERT INTO "livros" VALUES(47,'Mandisa e a Vovó Alegria','Vagner Amaro','Malê Mirim','Infanto‑Juvenil','Narrativa infantil com ancestralidade','Infantojuvenil','Mandisa convive com a avó Alegria, que transmite ensinamentos sobre ancestralidade, cultura e amor familiar por meio de objetos e memória.',24,'2023-07-10','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MandisaVovoAlegria.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MandisaVovoAlegria.jpeg?raw=true');
INSERT INTO "livros" VALUES(48,'Caderno de rimas do João','Lázaro Ramos','Pallas','Infanto‑Juvenil','Poesia infantil','Infantojuvenil','Primeiro livro infantil do autor, reúne rimas espontâneas sobre cotidiano, cultura e família, ilustrado por Mauricio Negro.',40,'2010-12-21','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CadernoRimasJoao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CadernoRimasJoao.jpeg?raw=true');
INSERT INTO "livros" VALUES(49,'Caderno sem rimas da Maria','Lázaro Ramos','Pallas','Infanto‑Juvenil','Poesia infantil criativa','Infantojuvenil','Inspirado na filha Maria, brinca com palavras inventadas e dá visibilidade à linguagem infantil plena de inventividade.',36,'2018‑02‑01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CadernoSemRimasMaria.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CadernoSemRimasMaria.jpeg?raw=true');
INSERT INTO "livros" VALUES(50,'Calu: uma menina cheia de histórias','Cássia Vale & Luciana Palmeira','Malê Editora','Infanto‑Juvenil','Narrativa infantil comunitária','Infantojuvenil','Calu transforma seu bairro em um museu a céu aberto, celebrando memória coletiva, pertencimento e pertencimento cultural.',32,'2017‑01‑01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CaluMeninaCheiaHistorias.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CaluMeninaCheiaHistorias.jpeg?raw=true');
INSERT INTO "livros" VALUES(51,'Antônia e os cabelos que carregavam os segredos do Universo','Alan Alves Brito','Artêrinha','Infanto‑Juvenil','Narrativa infantil sobre identidade negra','Infantojuvenil','Antônia, menina negra nordestina, usa seus cabelos crespos como porta‑voz de ancestralidade e indagações cósmicas, conectando identidade e ciência.',36,'2022‑02‑24','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AntoniaCabelosSegredosUniverso.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AntoniaCabelosSegredosUniverso.jpeg?raw=true');
INSERT INTO "livros" VALUES(52,'Chuva de manga','James Rumford','Brinque‑Book','Infanto‑Juvenil','Literatura infantil','Infantil','Tomás, menino chadiano, vive a espera da chuva que fará mangueiras florirem. Um relato sobre esperança, imaginação e vida no Chade.',32,'2005‑11‑04','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ChuvaDeManga.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ChuvaDeManga.jpeg?raw=true');
INSERT INTO "livros" VALUES(53,'Casas de Unzó','Ciano','Norte / Jandaíra','Infanto‑Juvenil','Narrativa periférica em realismo mágico','Infantojuvenil','Unzó e sua avó perdem a casa e vivem uma noite mágica de resistência, identidade e moradia por meio de símbolos e linguagens populares.',72,'2021‑11‑27','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CasasDeUnzo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CasasDeUnzo.jpeg?raw=true');
INSERT INTO "livros" VALUES(54,'Aqui no morro, tem princesa sim!','Simone Botelho','Albatrozinho','Infanto‑Juvenil','Narrativa infantil periférica','Infantojuvenil','Alika, menina negra periférica, sonha ser princesa de verdade e descobre sua ancestralidade e realeza nos ritmos do samba e na cultura popular.',40,'2022‑01‑11','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AquiNoMorroTemPrincesaSim.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AquiNoMorroTemPrincesaSim.jpeg?raw=true');
INSERT INTO "livros" VALUES(55,'Bruna e a galinha d Angola','Gercilga de Almeida','Pallas','Infanto‑Juvenil','Narrativa infantil mitológica','Infantojuvenil','Bruna cria um vínculo mágico com uma galinha d’angola, descobrindo lendas africanas, oralidade e pertencimento a partir da ancestralidade da avó.',24,'2009‑03‑19','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BrunaGalinhaDAngola.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BrunaGalinhaDAngola.jpeg?raw=true');
INSERT INTO "livros" VALUES(56,'Brincar na rua','Magna Domingues','Baú Encantado','Infanto‑Juvenil','Narrativa infantil periférica','Infantojuvenil','Maria Eunice e outras crianças da periferia se reúnem para brincar na rua, entre memórias e o direito de ocupar a cidade com autonomia e alegria.',22,'2022‑01‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BrincarNaRua.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BrincarNaRua.jpeg?raw=true');
INSERT INTO "livros" VALUES(57,'Omo: o rio da liberdade','Vanessa Alexandre','PAE Editora','Infanto‑Juvenil','Narrativa infantil sobre natureza e liberdade','Infantojuvenil','Sadiki e Ayana vivem às margens do rio Omo, na Nigéria; desconhecem a escravidão e são libertos pela natureza, criatividade e arte.',32,'2021‑01‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OmoRioLiberdade.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OmoRioLiberdade.jpeg?raw=true');
INSERT INTO "livros" VALUES(58,'Bolota: uma certa jabuticaba muito esperta','Iray Galrão','Escritinha','Infanto‑Juvenil','Poesia infantil / pictórica','Infantojuvenil','Bolota aprende sobre virtude das diferenças, igualdade racial e continuidade da vida ao observar sua jabuticabeira, com imagens bordadas feitas pela autora.',32,'2014‑01‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BolotaJabuticaba.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BolotaJabuticaba.jpeg?raw=true');
INSERT INTO "livros" VALUES(59,'Bucalá: a pequena princesa do Quilombo Cabula','Davi Nunes','Malê Mirim','Infanto‑Juvenil','Narrativa infantil com ancestralidade','Infantojuvenil','Bucalá vive no Quilombo Cabula e encanta com sua conexão com a natureza, fortalecimento de identidade quilombola e herança cultural.',32,'2023‑04‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BucalaPrincesaCabula.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BucalaPrincesaCabula.jpeg?raw=true');
INSERT INTO "livros" VALUES(60,'Imani','Marcos Cajé','Mostarda','Infanto‑Juvenil','Literatura infantil com ancestralidade','Infantojuvenil','Imani vive no quilombo Rio da Lua e deseja participar das rodas de capoeira; em sua jornada descobre que ver vai além dos olhos, construindo autonomia e magia.',24,'2024‑???‑??','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Imani.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Imani.jpeg?raw=true');
INSERT INTO "livros" VALUES(61,'Omo‑Obá; histórias de princesas','Kiusam de Oliveira','Mazza Edições','Infanto‑Juvenil','Coletânea ilustrada de mitos africanos','Infantojuvenil','Narrativas de seis princesas-príncipes iorubás (orixás) que enfrentam desafios, amadurecem e ressignificam seu lugar no mundo, com muita força e ancestralidade.',64,'2009‑01‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OmoOba.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OmoOba.jpeg?raw=true');
INSERT INTO "livros" VALUES(62,'África recontada para crianças','Avani Souza Silva','Martin Claret','Infanto‑Juvenil','Coletânea de contos africanos','Infantojuvenil','Seleção de fábulas e contos tradicionais de países africanos de língua portuguesa, com adivinhas, músicas, gastronomia e elementos culturais diversos.',120,'2020‑04‑01','2025‑07‑29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfricaRecontadaParaCriancas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfricaRecontadaParaCriancas.jpeg?raw=true');
INSERT INTO "livros" VALUES(63,'África: contos do rio, da selva e da savana','Silvana Salerno','Girassol','Infanto‑Juvenil','Antologia de contos africanos','Infantojuvenil','Coletânea de contos orais de diversos países africanos (Congo, Guiné‑Bissau, Benin, Nigéria, Angola, Moçambique, Senegal), resgatando griôs e tradição ancestral.',80,'2022‑11‑30','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfricaContosRioSelvaSavana.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfricaContosRioSelvaSavana.jpeg?raw=true');
INSERT INTO "livros" VALUES(64,'Pequenos contos negros','Blaise Cendrars','SM Educação','Infanto‑Juvenil','cultura afro','Infantojuvenil','Histórias inspiradas em fontes etnográficas africanas, misturando animais, seres sobrenaturais e folclore, pela primeira vez publicadas no Brasil.',96,'2014‑01‑01','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PequenosContosNegros.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PequenosContosNegros.jpeg?raw=true');
INSERT INTO "livros" VALUES(65,'África que você fala','Claudio Fragata','Globinho','Infanto-Juvenil','cultura afro','Infantojuvenil','De forma leve e divertida, A África que você fala faz um passeio por palavras que pegamos emprestadas de idiomas como quimbundo, iorubá, jeje e banto. ',40,'2021-01-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfricaQueVoceFala.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfricaQueVoceFala.jpeg?raw=true');
INSERT INTO "livros" VALUES(66,'Ruas do brincar','Leandro Rodrigues e Fernando Erminio','Coleção Literária Besouro','Infanto-Juvenil','Infanto-Juvenil','Infanto-Juvenil','Um passeio afetivo pelas brincadeiras tradicionais de rua, resgatando memórias e promovendo a valorização da cultura popular.',36,'2022-04-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/RuasDoBrincar.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/RuasDoBrincar.jpeg?raw=true');
INSERT INTO "livros" VALUES(67,'É besouro porque é preto e voa','Vários Autores','Coleção Literária Besouro','Infanto-Juvenil','Infanto-Juvenil','Infanto-Juvenil','Coletânea de histórias e poesias que celebram a cultura negra e a imaginação de crianças pretas que sonham e voam.',40,'2023-08-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EBesouroPorqueEPretoEVoa.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EBesouroPorqueEPretoEVoa.jpeg?raw=true');
INSERT INTO "livros" VALUES(68,'ÉDÁ, EDÁ – as palavras e cores da criação Òkè','Mariana Moretti e Luiz Carlos Garcia','Literatura Suburbana e Literária Besouro','Axé','Infantil','Infantil','Inspirado na cosmovisão africana, o livro apresenta de forma poética e visual os mitos da criação a partir das palavras e cores sagradas de Òkè.',34,'2022-12-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EdaEda.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EdaEda.jpeg?raw=true');
INSERT INTO "livros" VALUES(69,'As tranças de minha mãe','Ana Fátima','Ereginga Educação','maternidade','Literatura infantil','Infantil','Narrado pelo pequeno Akin, o livro celebra o vínculo entre mãe e filho por meio das tranças, símbolos de ancestralidade africana e afetividade.',32,'2021-03-05','2025-07-18','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TrancasMae.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TrancasMae.jpeg?raw=true');
INSERT INTO "livros" VALUES(70,'Ashanti, Nossa Pretinha','Taís Espirito Santo','Malê Mirim','Maternidade','Infantil / Cultura Afro-brasileira','Infantil','A história sensível de uma criança negra e sua relação de afeto com a mãe e sua ancestralidade.',28,'2022-03-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AshantiNossaPretinha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AshantiNossaPretinha.jpeg?raw=true');
INSERT INTO "livros" VALUES(71,'Mãe Dinha','Maria do Carmo','Maza Edições','Maternidade','Literatura Afro-brasileira','Infantil','Uma homenagem às mulheres negras que transmitem sabedoria, acolhimento e resistência por meio da maternidade.',30,'2021-06-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MaeDinha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MaeDinha.jpeg?raw=true');
INSERT INTO "livros" VALUES(72,'Cheirinho de Neném','Patrícia Santana','Maza Edições','Maternidade','Infantil','Infantil','Uma narrativa delicada sobre os primeiros momentos de vida e o vínculo entre mãe e bebê.',24,'2020-02-14','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CheirinhoDeNenem.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CheirinhoDeNenem.jpeg?raw=true');
INSERT INTO "livros" VALUES(73,'Mamãe Sangra','Cláudia Pacheco','Cláudia Pacheco','Maternidade','Educação / Corpo Humano','Infantil','Um livro que aborda a menstruação com naturalidade e afeto, ajudando crianças a compreenderem esse processo com empatia.',36,'2022-10-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MamaeSangra.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MamaeSangra.jpeg?raw=true');
INSERT INTO "livros" VALUES(74,'Lenço de Cetim da Mamãe','Chimamanda Ngozi Adichie','Companhia das Letrinhas','Maternidade','Infantil','Infantil','Uma história sensível sobre a relação entre mãe e filha, com toques culturais e afetivos.',20,'2019-06-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LencoDeCetimDaMamae.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LencoDeCetimDaMamae.jpeg?raw=true');
INSERT INTO "livros" VALUES(75,'Juntos na aldeia','Lúis Donisete Benzi Grupioni','Berlendis & Vertecchia','Cultura Indígena','Infantil','Infantil','Um olhar sensível sobre o cotidiano de uma aldeia indígena, destacando a importância da coletividade e das tradições.',32,'2022-05-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/JuntosNaAldeia.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/JuntosNaAldeia.jpeg?raw=true');
INSERT INTO "livros" VALUES(76,'Curumim que virou gigante','Joel Rufino do Santos','Ática','Cultura Indígena','Infanto-Juvenil','Infanto-Juvenil','Inspirado nas lendas indígenas, conta a história de um curumim que se transforma em gigante para proteger seu povo.',28,'2021-11-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CurumimQueVirouGigante.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CurumimQueVirouGigante.jpeg?raw=true');
INSERT INTO "livros" VALUES(77,'Clara como Sol; escuro como a noite','José Bortolini e Pallus',' Paulus','Cultura Indígena','Infantil','Infantil','Uma narrativa que explora as diferenças, a ancestralidade e o respeito entre culturas, inspirada nos saberes indígenas.',30,'2023-03-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ClaraComoSol.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ClaraComoSol.jpeg?raw=true');
INSERT INTO "livros" VALUES(78,'Kariri Xocó; contos indígenas','Denízia Cruz','Estúdio Kiwi','Cultura Indígena','Infantil','Infantil','Coletânea de contos que apresentam a riqueza da cultura e espiritualidade do povo Kariri Xocó.',34,'2022-08-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/KaririXoco.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/KaririXoco.jpeg?raw=true');
INSERT INTO "livros" VALUES(79,'Pia o pio; pia Martim','Karina Ferro Otauka','Projeto Contemplado','Cultura Indígena','Infantil','Infantil','Uma história poética sobre o despertar da natureza e os sons da floresta, conectando o leitor à cosmovisão indígena.',36,'2021-09-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PiaOPio.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PiaOPio.jpeg?raw=true');
INSERT INTO "livros" VALUES(80,'Mani; a origem da mandioca','Lenda Guarani','Pallus','Cultura Indígena','Infantil','Infantil','Reconta a tradicional lenda Guarani sobre Mani, a menina que deu origem à mandioca, um dos alimentos mais importantes da cultura indígena.',24,'2020-06-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Mani.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Mani.jpeg?raw=true');
INSERT INTO "livros" VALUES(81,'Povos Indígenas no Brasil','Rosa Gauditano','Caixa Cultural','Cultura Indígena','Infantil','Infantil','Uma introdução à diversidade dos povos indígenas brasileiros com fotos, histórias e saberes tradicionais.',42,'2019-03-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PovosIndigenas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PovosIndigenas.jpeg?raw=true');
INSERT INTO "livros" VALUES(82,'Viagem ao mundo indígena','Lúis Donisete Benzi Grupioni','Berlendis & Vertecchia','Cultura Indígena','Infantil','Infantil','Uma jornada educativa pelas culturas indígenas brasileiras, promovendo o respeito e a valorização da diversidade.',38,'2021-04-18','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ViagemMundoIndigena.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ViagemMundoIndigena.jpeg?raw=true');
INSERT INTO "livros" VALUES(83,'Lenda do Sol e da Lua','Hardy Guedes','HGF','Cultura Indígena','Infantil','Infantil','Uma bela lenda indígena que narra a origem do Sol e da Lua, trazendo ensinamentos sobre harmonia e dualidade.',20,'2020-01-08','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LendaSolLua.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LendaSolLua.jpeg?raw=true');
INSERT INTO "livros" VALUES(84,'Saguairu','Júlio Emílio Braz','Mostarda','Cultura Indígena','Infantil','Infantil','Inspirado em lendas indígenas, o livro conta a história de Saguairu, um jovem que aprende sobre coragem e ancestralidade.',36,'2018-06-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Saguairu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Saguairu.jpeg?raw=true');
INSERT INTO "livros" VALUES(85,'Tatu Balão','Sonia Barros','Aletria','Coleção Literária Itaú - Leia para uma criança','Infantil','Infantil','Um tatu sonhador transforma um simples balão em uma grande aventura, incentivando a imaginação e a criatividade das crianças.',28,'2017-09-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TatuBalao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TatuBalao.jpeg?raw=true');
INSERT INTO "livros" VALUES(86,'A Visita','Antje Damm','Companhia das Letrinhas','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / emoções','Infantojuvenil','Elise é uma mulher muito medrosa que vive isolada até que um aviãozinho de papel e uma visita inesperada a desafiam a sair da zona de conforto, promovendo transformação emocional.',40,'2016-06-08','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AVisita.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AVisita.jpeg?raw=true');
INSERT INTO "livros" VALUES(87,'O Tupi que Você Fala','Cláudio Fragata','Globinho','Coleção Literária Itaú - Leia para uma criança','Literatura infantil','Infantil','Mostra como palavras do cotidiano têm origem indígena (como pipoca, saci, abacaxi), revelando que o tupi está vivo em nosso português de forma divertida e ilustrada.',32,'2015-03-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OTupiQueVoceFala.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OTupiQueVoceFala.jpeg?raw=true');
INSERT INTO "livros" VALUES(88,'Selou e Maya','Lara Meana','SM Educação','Coleção Literária Itaú - Leia para uma criança','Narrativa infantil / fantasia cotidianas','Infantojuvenil','Relata as fantasias de duas vizinhas: Selou brinca de galopar na savana ou assustar crocodilos, enquanto Maya imagina rodar no carrossel do Central Park ou nadar no rio Nilo — celebrando a imaginação e amizade.',32,'2013-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/SelouEMaya.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/SelouEMaya.jpeg?raw=true');
INSERT INTO "livros" VALUES(89,'Chapeuzinho amarelo','Chico Buarque','Autêntica infantil e juvenil','Coleção Literária Itaú - Leia para uma criança','Literatura infantil','Infantil','Chapeuzinho é tão medrosa que tem medo até do próprio medo. Ao enfrentar o Lobo, ela aprende a superar seus temores e voltar a viver com alegria.',36,'2017-03-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ChapeuzinhoAmarelo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ChapeuzinhoAmarelo.jpeg?raw=true');
INSERT INTO "livros" VALUES(90,'Com que roupa irei para a festa do rei?','Tino Freitas','Editora do Brasil','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / poesia','Infantil','Em versos divertidos e ilustrações encantadoras, animais tentam adivinhar a roupa que o rei usará na festa — inspirada no conto de Andersen, com humor e referências culturais.',40,'2017-?-?','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ComQueRoupaIreiParaAFestaDoRei.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ComQueRoupaIreiParaAFestaDoRei.jpeg?raw=true');
INSERT INTO "livros" VALUES(91,'Dorme, menino, dorme','Laura Herrera','Livros da Matriz','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / cantiga de ninar','Infantil','Com versos rimados e ilustrações sensíveis, acompanha um menino que não consegue dormir enquanto tenta se recolher ao mundo dos sonhos — uma cantiga reconfortante.',32,'2014-?-?','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DormeMeninoDorme.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DormeMeninoDorme.jpeg?raw=true');
INSERT INTO "livros" VALUES(92,'Em cima daquela serra','Eucanaã Ferraz','Companhia das Letrinhas','Coleção Literária Itaú - Leia para uma criança','Poesia infantil','Infantil','Poema visual sobre o que passa por cima de uma serra — bois, balões, caminhões e até um elefante — com ritmo encantador e ilustrações poéticas de Yara Kono.',40,'2013-02-19','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EmCimaDaquelaSerra.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EmCimaDaquelaSerra.jpeg?raw=true');
INSERT INTO "livros" VALUES(93,'Enquanto o almoço não fica pronto','Sonia Rosa','Zit Editora','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / cotidiano afetivo','Infantil','Com poesia e humor, retrata a correria tranquila de uma família enquanto o almoço ainda não está pronto, exaltando o afeto doméstico.',24,'2020-??-??','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EnquantoOAlmocoNaoFicaPronto.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EnquantoOAlmocoNaoFicaPronto.jpeg?raw=true');
INSERT INTO "livros" VALUES(94,'Festa no céu','Ângela Lago','Melhoramentos','Coleção Literária Itaú - Leia para uma criança','Folclore infantil ilustrado','Infantil','Adaptação do folclore em que animais sem asas desejam ir à festa no céu; uma tartaruga esperta consegue driblar a impossibilidade e participar da celebração.',32,'2019-04-04','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/FestaNoCeu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/FestaNoCeu.jpeg?raw=true');
INSERT INTO "livros" VALUES(95,'Gato pra cá; rato pra lá','Sylvia Orthof','Rovelle','Coleção Literária Itaú - Leia para uma criança','Fábula poética infantil','Infantil','Poética história noturna de um gato viajado que encontra um rato triste; o encontro desperta empatia e beleza na simplicidade da vida.',21,'2012-??-??','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/GatoPraCaRatoPraLa.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/GatoPraCaRatoPraLa.jpeg?raw=true');
INSERT INTO "livros" VALUES(96,'Jogo da parlenda','Heloisa Pietro','Scipione','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / parlendas','Infantil','Uma coletânea divertida de parlendas brasileiras para brincar, aprender e se divertir com as rimas tradicionais.',32,'2015-06-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/JogoDaParlenda.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/JogoDaParlenda.jpeg?raw=true');
INSERT INTO "livros" VALUES(97,'Léo e a baleia','Benjin Davis','Callis Editora','Coleção Literária Itaú - Leia para uma criança','Literatura infantil / conto ilustrado','Infantil','A história emocionante de um menino chamado Léo que sonha em conhecer uma baleia e vive uma aventura cheia de aprendizado e amizade.',28,'2018-03-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/LeoEBaleia.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/LeoEBaleia.jpeg?raw=true');
INSERT INTO "livros" VALUES(98,'Lino','André Neves','Callis','Coleção Literária Itaú - Leia para uma criança','Literatura infantil','Infantil','Lino é a história de um porquinho que fez amizade com uma coelhinha branca chamada Lua na fábrica de brinquedos. Depois que Lua desaparece, Lino sente falta dela — quem seria seu companheiro de corridas e de histórias na hora de dormir? O livro trata de amizade, saudade e superação com linguagem acessível para crianças pequenas.',36,'2010-06-28','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/lino.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/lino.jpeg?raw=true');
INSERT INTO "livros" VALUES(99,'O Menino Azul','Cecília Meireles','Global Editora','Coleção Literária Itaú - Leia para uma criança','Poesia infantil','Literatura infantil / Poesia','Um poema infantil que expressa o imaginário de uma criança que deseja um burrinho capaz de inventar histórias bonitas com pessoas, bichos e barquinhos no mar. A linguagem é leve, rica em imagens e simbolismo, encantando crianças, jovens e adultos.',24,'2013-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/o_menino_azul.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/o_menino_azul.jpeg?raw=true');
INSERT INTO "livros" VALUES(100,'O Mundo Inteiro','Liz Garton Scanlon','Paz & Terra','Coleção Literária Itaú – Leia para uma Criança','Poesia infantil / Literatura infantil','Infantil','Livro poético que explora o que constitui “o mundo inteiro” por meio de versos rimados e ilustrações delicadas, celebrando a vida, as conexões humanas e os pequenos detalhes do dia a dia.',40,'2012-12-31','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/o_mundo_inteiro.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/o_mundo_inteiro.jpeg?raw=true');
INSERT INTO "livros" VALUES(101,'Lobisomem','Equipe Girassol','Girassol / Itaú Social','Coleção Literária Itaú – Leia para uma Criança','Literatura','infantil','A lenda do lobisomem reimaginada de forma leve e acessível: quando um oitavo filho completa 13 anos, se transforma em lobisomem e enfrenta um grande desafio para voltar ao normal.',16,'2010-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/lobisomem.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/lobisomem.jpeg?raw=true');
INSERT INTO "livros" VALUES(102,'O ratinho, o morango vermelho maduro e o grande urso esfomeado','Don & Audrey Wood','Brinque-Book','Coleção Literária Itaú – Leia para uma Criança','Fábula infantil','infantil','Uma fábula repleta de humor sobre um ratinho tentando proteger seu morango de um narrador astuto e de um urso faminto que, curiosamente, não aparece.',34,'2012-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/o_ratinho_morango_urso.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/o_ratinho_morango_urso.jpeg?raw=true');
INSERT INTO "livros" VALUES(103,'Olhos do Jaguar','Yaguarê Yamã','Jujuba Editora','Coleção Itaú – Leia para uma Criança','Fábula / Literatura infantil indígena','infantil','Fábula amazônica contada em linguagem maraguá: um jaguar tenta enganar um tatu, acaba perdendo seus olhos, mas é salvo por um pássaro amigo, numa história cheia de simbolismo e respeito à cultura indígena.',24,'2021-09-16','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/olhos_do_jaguar.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/olhos_do_jaguar.jpeg?raw=true');
INSERT INTO "livros" VALUES(104,'Papai!','Philippe Corentin','Cosac Naify (edição Itaú Social)','Coleção Itaú – Leia para uma Criança','Literatura infantil','infantil','Uma história leve e divertida sobre a relação com o pai — com humor e simplicidade, típica do autor Philippe Corentin.',32,'2008-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/papai.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/papai.jpeg?raw=true');
INSERT INTO "livros" VALUES(105,'Pedro vira porco-espinho','Janaina Tokitaka','Jujuba Editora','Coleção Literária Itaú – Leia para uma Criança','Literatura infantil / Metáfora emocional','infantil','Pedro é um menino normal que, em um dia frustrante, “vira porco-espinho” — uma metáfora divertida sobre como reagimos aos nossos sentimentos e emoções.',31,'2018-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/pedro_vira_porco_espinho.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/pedro_vira_porco_espinho.jpeg?raw=true');
INSERT INTO "livros" VALUES(106,'Poeminhas da Terra','Márcia Leite (ilustração Tatiana Móes)','Pulo do Gato','Coleção Literária Itaú – Leia para uma Criança','Poesia infantil / Cultura indígena','infantil','Versos curtos em tupi-guarani que celebram o cotidiano da vida na aldeia, evocando cultura, natureza e identidade dos primeiros habitantes do Brasil.',32,'2016-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/poeminhas_da_terra.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/poeminhas_da_terra.jpeg?raw=true');
INSERT INTO "livros" VALUES(107,'Poesia na Varanda','Sônia Junqueira (ilustração Flávio Fargas)','Autêntica Infantil e Juvenil','Coleção Literária Itaú - Leia para uma Criança','Poesia infantil','infantil','Poemas que revelam a poesia em elementos simples do cotidiano - plantas, chuva, noite estrelada - celebrando a descoberta poética através dos sentidos.',28,'2011-02-08','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/poesia_na_varanda.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/poesia_na_varanda.jpeg?raw=true');
INSERT INTO "livros" VALUES(108,'Quero colo!','Stela Barbieri & Fernando Vilela','SM Edições','Coleção Literária Itaú - Leia para uma Criança','Literatura infantil / Afeto','infantil','Texto que apresenta o colo nas mais diversas culturas e contextos - humano e animal - em cenas que ressaltam afeto, cuidado e acolhimento.',36,'2016-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/quero_colo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/quero_colo.jpeg?raw=true');
INSERT INTO "livros" VALUES(109,'Árvore dos sonhos','Fabiano Alves Onça','Banco Itaú (Meu Dinheirinho)','Coleção Literária Itaú - Leia para uma Criança','Literatura infantil / Educação financeira','infantil','Livro infantil que apresenta o tema da educação financeira de forma lúdica e acessível, por meio de sonhos que crescem como árvores.',32,'2012-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/arvore_dos_sonhos.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/arvore_dos_sonhos.jpeg?raw=true');
INSERT INTO "livros" VALUES(110,'Adivinha quanto eu te amo','Sam McBratney (ilustração: Anita Jeram)','Editora (edição brasileira)','Coleção Literária Itaú - Leia para uma Criança','Literatura infantil / Afeto','infantil','Coelhinho e Papai Coelho medem - em gestos e palavras - o tamanho do amor, numa história doce e envolvente que celebra o afeto entre pais e filhos.',36,'2019-01-01','2025-08-12','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/adivinha_quanto_eu_te_amo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/adivinha_quanto_eu_te_amo.jpeg?raw=true');
INSERT INTO "livros" VALUES(111,'Sinto o que sinto: um passeio pelos sentimentos','Lázaro Ramos','Sextante','Comportamento / Sentimentos','Narrativa sensorial infantil','infantil','Dan, com ajuda do Mundo Bita, explora emoções como raiva, alegria, tristeza e orgulho, aprendendo a reconhecê-las e aceitá-las como parte natural da vida.',48,'2025-02-05','2025-07-29','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/SintoOQueSinto.jpg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/SintoOQueSinto.jpeg?raw=true');
INSERT INTO "livros" VALUES(112,'Menina Nicinha','Evelyn Sacramento','Lendo Mulheres Negras / Aldir Blanc Bahia','manifestação cultural','Narrativa infantil com memória cultural','Infantojuvenil','Relatos da infância da autora no recôncavo baiano, inspirando mulheres negras através de memórias e cultura afro-brasileira.',36,'2021-06-01','2025‑07‑28','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninaNicinha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninaNicinha.jpeg?raw=true');
INSERT INTO "livros" VALUES(113,'A Dáie de Vários Nomes','Taslim','Kitembo','Afrofuturismo','Ficção infantojuvenil afrofuturista','Infantojuvenil','Maisha é uma dáie que viaja no tempo para registrar eventos da pandemia de 2020 no Rio de Janeiro – uma ficção afrofuturista que mistura memórias, identidade negra e viagem temporal.',160,'2024-09-07','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ADaieDeVariosNomes.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ADaieDeVariosNomes.jpeg?raw=true');
INSERT INTO "livros" VALUES(114,'Pensamentos Afrofuturistas','Lu Ain Zaila (org.)','Kitembo','Afrofuturismo','Ensaio literário / antologia afrofuturista','Infantojuvenil','Antologia organizada por Lu Ain Zaila com textos e reflexões de artistas e artivistas negros que exploram o Afrofuturismo à brasileira de forma poética e engajada.',144,'2025-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PensamentosAfrofuturistas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PensamentosAfrofuturistas.jpeg?raw=true');
INSERT INTO "livros" VALUES(115,'Máscaras do Poder: A Origem','Márcio Roberto do Carmo','Mostarda','Afrofuturismo','HQ / Ficção infantil afrocêntrica','Infantojuvenil','História em quadrinhos que traz máscaras místicas inspiradas na mitologia africana, conferindo poderes ancestrais a jovens protagonistas negros que enfrentam ameaça global.',64,'2024-04-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MascarasDoPoder.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MascarasDoPoder.jpeg?raw=true');
INSERT INTO "livros" VALUES(116,'Beto, o garoto batuque','Rodrigo Mendes & Thaís Ferreira','Kitembo','Afrofuturismo','Literatura ilustrada infantil com afrofuturismo','Infantojuvenil','Beto adora batucar e sambar, unindo afeto e ancestralidade em uma batucada na escola — uma história visual com estética afrofuturista e celebração da cultura negra.',48,'2024-11-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BetoOGarotoBatuque.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BetoOGarotoBatuque.jpeg?raw=true');
INSERT INTO "livros" VALUES(117,'Ubuntu 2048','Raphael Silva & Inara Régia (Isdruxula)','Kitembo','Afrofuturismo','Ficção cyberpunk afrofuturista juvenil','Infantojuvenil','Em um futuro distópico, Teshai e a androide Kinaya enfrentam desafios para manter a filosofia Ubuntu viva — uma trama afrofuturista sobre identidade, tecnologia e solidariedade.',204,'2021-05-19','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Ubuntu2048.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Ubuntu2048.jpeg?raw=true');
INSERT INTO "livros" VALUES(118,'Cabeça de Zumbi','Israel Neto','Kitembo','Afrofuturismo','Suspense afrofuturista infantojuvenil','Infantojuvenil','Uma sociedade secreta busca garantir a passagem de Zumbi dos Palmares pela Kalunga. Jornalistas precisam usar suas vozes para preservar a memória e enfrentar uma trama ancestral.',124,'2024-08-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CabecaDeZumbi.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CabecaDeZumbi.jpeg?raw=true');
INSERT INTO "livros" VALUES(119,'Por um fio','Laura de Paula & Will Rez','Kitembo','Afrofuturismo','Conto afrofuturista infantojuvenil','Infantojuvenil','Conto afrofuturista que mescla realismo mágico e especulação tecnológica, com protagonistas negras em um universo permeado por ancestralidade e resiliência.',NULL,'2024-??-??','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PorUmFio.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PorUmFio.jpeg?raw=true');
INSERT INTO "livros" VALUES(120,'Omi Ipalolo: águas silenciosas','Will Braga & Douglas S. T. Reverie','Kitembo','Afrofuturismo','Afrofuturismo quântico / graphic novel','Infantojuvenil','Expressionismo afro‑surreal se encontra ao futurismo quântico africano em uma jornada sobre colonização, mutação humana e resistências multissem domínio do multiverso.',148,'2022-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/OmiIpaloloAguasSilenciosas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/OmiIpaloloAguasSilenciosas.jpeg?raw=true');
INSERT INTO "livros" VALUES(121,'Almanaque Kitembo 2K23 - Quadrinhos Afrofuturistas','Vários autores','Kitembo','Afrofuturismo','Antologia em HQ / quadrinhos infantis afrofuturistas','Infantojuvenil','Volume anual com nove histórias em quadrinhos de criadoras e criadores negros por todo o Brasil, explorando propostas estéticas, políticas e afrofuturas narrativas plurais.',150,'2024-04-24','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AlmanaqueKitembo2K23.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AlmanaqueKitembo2K23.jpeg?raw=true');
INSERT INTO "livros" VALUES(122,'3 Esús e o Tempo: o mundo de Eleguá','Israel Neto & Rodrigo Cândido','Kitembo','Afrofuturismo','Graphic novel / quadrinhos especulativos africanos','Infantojuvenil','Eleguá se torna um andarilho em um mundo devastado por uma chuva interminável; retirante ancestral que recorre à profecia, herança espiritual e resistência para restaurar equilíbrio.',44,'2023-12-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/3EsusOTempoElegua.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/3EsusOTempoElegua.jpeg?raw=true');
INSERT INTO "livros" VALUES(123,'Ancestral','Israel Neto & Gê Mendes','Kitembo','Afrofuturismo','Space opera afrofuturista','Infantojuvenil','Uma space opera ambiciosa que entrelaça civilizações humanas em planetas do sistema estelar de Rá com conhecimentos ancestrais africanos, questionamentos sobre colonização galáctica e esperança coletiva.',230,'2021-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Ancestral.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Ancestral.jpeg?raw=true');
INSERT INTO "livros" VALUES(124,'Afrofuturismo: O Futuro é Nosso – Vol. 1','Vários autores','Kitembo','Afrofuturismo','Antologia afrofuturista infantil','Infantojuvenil','Coletânea com contos que exploram futuros possíveis a partir de perspectivas afro-brasileiras — tecnologia ancestral, representatividade e inovação cultural, valorizando a imaginação negra.',108,'2020-??-??','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfrofuturismoOFuturoENossoVol1.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfrofuturismoOFuturoENossoVol1.jpeg?raw=true');
INSERT INTO "livros" VALUES(125,'Afrofuturismo: O Futuro é Nosso – Vol. 3','Vários autores','Kitembo','Afrofuturismo','Antologia em HQ / textos infantis','Infantojuvenil','Terceiro volume da série antológica afrofuturista da Kitembo, com nove contos e histórias em quadrinhos escritos e ilustrados por autores negros do Brasil, destacando identidades negras e imaginação futurista.',116,'2024-04-24','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfrofuturismoOFuturoENossoVol3.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfrofuturismoOFuturoENossoVol3.jpeg?raw=true');
INSERT INTO "livros" VALUES(126,'Akin: o rei de Igbo','Marcos Cajé','Malê Mirim','Afrofuturismo','Ficção infantojuvenil mitológica africana','Infantojuvenil','Akin recebe a profecia de salvar o reino Igbo, embarcando numa viagem espiritual com ensinamentos dos mitos africanos até tornar-se líder e guerreiro ancestral.',54,'2020-10-01','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AkinOReiDeIgbo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AkinOReiDeIgbo.jpeg?raw=true');
INSERT INTO "livros" VALUES(127,'Antologia Afro‑Caribenha: ficção científica e literatura fantástica (edição República Dominicana)','Vários autores','Kitembo / ADFE','Afrofuturismo','Antologia de ficção especulativa afro‑caribenha','Infantojuvenil','Contos especulativos de autoras e autores negros da República Dominicana, transitando entre Afrofuturismo, Taíno futurismo e víru­s‑punk.',102,'2023-01-01','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AntologiaAfroCaribenha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AntologiaAfroCaribenha.jpeg?raw=true');
INSERT INTO "livros" VALUES(128,'Afrofuturo ancestral do amanhã','Henrique André & Tutano Nômade','Kitembo','Afrofuturismo','Poema ilustrado afrofuturista','Infantojuvenil','A professora Amara convida crianças a projetarem o futuro a partir da ancestralidade negra — uma introdução afrofuturista para crianças sonhadoras.',44,'2022-08-29','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfrofuturoAncestralDoAmanha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfrofuturoAncestralDoAmanha.jpeg?raw=true');
INSERT INTO "livros" VALUES(129,'Afrofuturismo: O Futuro é Nosso – Vol. 2','Vários autores','Kitembo','Afrofuturismo','Antologia infantojuvenil de contos afrofuturistas','Infantojuvenil','Volume com contos de jovens autores negros brasileiros ambientados em universos futuristas, space‑opera, tramas policiais e androides.',126,'2020‑01‑01','2025‑07‑30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AfrofuturismoOFuturoENossoVol2.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AfrofuturismoOFuturoENossoVol2.jpeg?raw=true');
INSERT INTO "livros" VALUES(130,'Os 3 Esús e o Tempo','Israel Neto & Rodrigo Cândido','Kitembo','Afrofuturismo','Graphic novel / HQ afrofuturista','Infantojuvenil','Uma aventura afrofuturista em que um aprendiz de Orunmilá quebra o tempo, promovendo o encontro de três dimensões e refletindo sobre legados, ancestralidade e profecia.',40,'2021-01-08','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Os3EsusETempo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Os3EsusETempo.jpeg?raw=true');
INSERT INTO "livros" VALUES(131,'(Amor)Talhamento e Os Fantasmas da Rua Sete','Juciane Reis & Danilo Celso','Kitembo / Festival Literaturas Negras','Afrofuturismo','Coletânea infantojuvenil afrofuturista','Infantojuvenil','Volume 1 da Coleção ‘Literaturas Negras’, reúne dois contos: (Amor)Talhamento, sobre luto e feminicídio negro, e Os Fantasmas da Rua Sete, que entrelaça ancestralidade, medicina e reencarnação.',116,'2021-07-17','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AmorTalhamentoFantasmaRuaSete.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AmorTalhamentoFantasmaRuaSete.jpeg?raw=true');
INSERT INTO "livros" VALUES(132,'Rebouças André e Antônio Rebouças','Júlio Emílio Braz','Mostarda','Coleção Black Power','Infantil','Infantil','Narrativa sobre os irmãos André e Antônio Rebouças, engenheiros negros que se destacaram na história do Brasil Império.',28,'2021-03-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ReboucasAndreEAntonio.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ReboucasAndreEAntonio.jpeg?raw=true');
INSERT INTO "livros" VALUES(133,'Tubman Harriet','Majori Silva','Mostarda','Coleção Black Power','Infantil','Infantil','A história inspiradora de Harriet Tubman, mulher negra que lutou contra a escravidão e se tornou símbolo de liberdade.',30,'2021-03-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TubmanHarriet.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TubmanHarriet.jpeg?raw=true');
INSERT INTO "livros" VALUES(134,'Almanaque Kitembo: Quadrinhos Afrofuturismol 2k24','Vários autores','Kitembo','Afrofuturismo','Quadrinhos / HQ afrofuturista','Infantojuvenil','Edição especial do Almanaque Kitembo com histórias em quadrinhos que exploram o afrofuturismo sob múltiplos olhares. Reúne artistas negros em narrativas que mesclam ancestralidade, tecnologia e crítica social.',48,'2024-03-22','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Afrofuturismol2k24.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Afrofuturismol2k24.jpeg?raw=true');
INSERT INTO "livros" VALUES(135,'Almanaque Kitembo: Quadrinhos Afrofuturismo 2k22','Vários autores','Kitembo','Afrofuturismo','Quadrinhos / HQ afrofuturista','Infantojuvenil','Primeira edição do Almanaque Kitembo com histórias em quadrinhos que exploram o afrofuturismo por meio de personagens negros, mitologias afro-brasileiras e ficção especulativa.',52,'2022-11-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AlmanaqueKitembo2k22.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AlmanaqueKitembo2k22.jpeg?raw=true');
INSERT INTO "livros" VALUES(136,'Multiverso em Multiverso','Poeta Afroluffy','Independente','Afrofuturismo','Poesia especulativa','Adulto','Obra poética que atravessa tempos, dimensões e identidades negras. Com uma linguagem futurista e lírica, o autor questiona a realidade e propõe existências múltiplas, afrorreferenciadas e interplanetárias.',110,'2023-12-08','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MultiversoEmMultiverso.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MultiversoEmMultiverso.jpeg?raw=true');
INSERT INTO "livros" VALUES(137,'Crianças nas Sombras','Hedjan CS e Amora Moreira','Kitembo','Afrofuturismo','Romance distópico juvenil','Infantojuvenil','Uma distopia afrofuturista em que crianças negras com dons especiais enfrentam um mundo que as quer invisíveis. A obra denuncia o racismo estrutural e propõe resistência por meio da imaginação.',168,'2024-05-17','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CriancasNasSombras.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CriancasNasSombras.jpeg?raw=true');
INSERT INTO "livros" VALUES(138,'Caçador Cibernético da Rua 13','Fábio Kabral','Malê Mirim','Afrofuturismo','Ficção científica afrofuturista','Juvenil / Adulto','Em uma metrópole futurista do Brasil, um jovem caçador de recompensas se envolve em uma missão que coloca à prova sua ancestralidade e seu vínculo com as tecnologias dos orixás.',180,'2017-10-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CacadorCiberneticoRua13.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CacadorCiberneticoRua13.jpeg?raw=true');
INSERT INTO "livros" VALUES(139,'Uzuri: A Guerreira de Ketu','Marcos Cajé','Quadrinhos da Mostarda','Afrofuturismo','Quadrinhos / HQ','Infantojuvenil','Uzuri, uma jovem guerreira do Reino de Ketu, usa armas tecnológicas e conhecimentos ancestrais para proteger seu povo. Uma aventura afrofuturista repleta de ação e mitologia afro-brasileira.',48,'2023-03-21','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/UzuriGuerreiraDeKetu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/UzuriGuerreiraDeKetu.jpeg?raw=true');
INSERT INTO "livros" VALUES(140,'A Cientista Guerreira do Facão Furioso','Fábio Kabral','Malê Mirim','Afrofuturismo','Ficção científica','Juvenil / Adulto','Um afrofuturo em que uma cientista se transforma em guerreira para proteger sua comunidade. Repleto de referências às culturas afro-brasileiras e orixás, a obra mistura ciência, resistência e misticismo.',196,'2020-09-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/CientistaGuerreiraFacaoFurioso.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/CientistaGuerreiraFacaoFurioso.jpeg?raw=true');
INSERT INTO "livros" VALUES(141,'Eu Conheço Uzomi','Kinaya','Kitembo','Afrofuturismo','Ficção afrofuturista','Infantojuvenil','A história de Uzomi é uma celebração da ancestralidade, identidade e tecnologia em um mundo onde o futuro ecoa as raízes africanas.',32,'2023-05-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EuConhecoUzomi.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EuConhecoUzomi.jpeg?raw=true');
INSERT INTO "livros" VALUES(142,'Imortal','Sabine Mendes','Kitembo','Afrofuturismo','Ficção científica','Juvenil','Imortal é uma jornada pelo tempo e espaço conduzida por saberes ancestrais e resistências negras. Uma narrativa potente sobre o legado imaterial africano no futuro.',140,'2022-11-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Imortal.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Imortal.jpeg?raw=true');
INSERT INTO "livros" VALUES(143,'Pantera Negra: O Jovem Príncipe','Ronald L. Smith','Excelsior Book One','Afrofuturismo','Aventura juvenil / ficção especulativa Marvel','Juvenil','T’Challa é um jovem príncipe de Wakanda que é enviado para estudar em Chicago. Entre experiências típicas da adolescência e missões heroicas, ele precisa encontrar seu lugar, esconder sua identidade real e enfrentar forças místicas e ameaças sobrenaturais.',208,'2020-03-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PanteraNegraOJovemPrincipe.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PanteraNegraOJovemPrincipe.jpeg?raw=true');
INSERT INTO "livros" VALUES(144,'Ubuntu','Raphael Silva','Kitembo','Afrofuturismo','Ficção afrofuturista','Infantojuvenil','Uma jornada afrofuturista sobre coletividade, tecnologia ancestral e conexão espiritual, onde o jovem protagonista redescobre a força de seus ancestrais para transformar o futuro.',96,'2022-10-18','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Ubuntu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Ubuntu.jpeg?raw=true');
INSERT INTO "livros" VALUES(145,'Há um monstro debaixo da minha cama','Clemency Pearce','Ciranda Cultural','Comportamento / Sentimentos','Literatura infantil','Infantil','Uma história divertida e sensível sobre enfrentar medos noturnos, mostrando que o que parece assustador pode ser só imaginação.',32,'2017-03-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/HaUmMonstroDebaixoDaMinhaCama.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/HaUmMonstroDebaixoDaMinhaCama.jpeg?raw=true');
INSERT INTO "livros" VALUES(146,'Fantasma Equilibrista','Tânia Alexandre Martinelli','Positivo','Comportamento / Sentimentos','Literatura infantojuvenil','Infantojuvenil','A história de um fantasma que sonha em ser equilibrista, mostrando que coragem e persistência são essenciais para superar inseguranças.',48,'2015-05-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/FantasmaEquilibrista.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/FantasmaEquilibrista.jpeg?raw=true');
INSERT INTO "livros" VALUES(147,'Ela e os Chega do Mimimi: A Busca pela Palavra Secreta','Eliane Ramos de Sousa','Dialética','Comportamento / Sentimentos','Literatura juvenil contemporânea','Juvenil','Em uma jornada criativa e cheia de reflexões, uma garota enfrenta dilemas emocionais e sociais em busca da palavra que transforma relações.',92,'2021-08-30','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ElaEOsChegaDoMimimi.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ElaEOsChegaDoMimimi.jpeg?raw=true');
INSERT INTO "livros" VALUES(148,'Ernesto','Biandina Franco','Companhia das Letrinhas','Comportamento / Sentimentos','Literatura infantil','Infantil','A história sensível de Ernesto, um menino que aprende a lidar com suas emoções e encontrar seu lugar no mundo.',36,'2013-10-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Ernesto.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Ernesto.jpeg?raw=true');
INSERT INTO "livros" VALUES(149,'O Monstro das Cores','Anna Llenas','Aletria','Comportamento / Sentimentos','Literatura infantil','Infantil','O Monstro das Cores está confuso e precisa entender seus sentimentos com a ajuda das cores. Uma história encantadora sobre educação emocional.',48,'2015-03-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MonstroDasCores.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MonstroDasCores.jpeg?raw=true');
INSERT INTO "livros" VALUES(150,'Não Fui Eu! Aprendendo Sobre Honestidade','Brian Moses e Mike Gordon','Scipione','Comportamento / Sentimentos','Literatura educativa infantil','Infantil','Com histórias do dia a dia, o livro ensina às crianças o valor da honestidade e a importância de assumir a responsabilidade por seus atos.',32,'2010-06-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/NaoFuiEu.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/NaoFuiEu.jpeg?raw=true');
INSERT INTO "livros" VALUES(151,'Jogo limpo, diversão garantida','Daniel Grippo','Terapia Infantil','Comportamento / Sentimentos','Literatura educativa infantil','Infantil','Este livro ensina às crianças a importância do jogo limpo e do respeito nas brincadeiras, promovendo valores como empatia e honestidade.',32,'2011-04-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/JogoLimpo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/JogoLimpo.jpeg?raw=true');
INSERT INTO "livros" VALUES(152,'O que Fazer Quando Você se Preocupa Demais: Um Guia para as Crianças Superarem a Ansiedade','Dawn Huebner','Artmed','Comportamento / Sentimentos','Autoajuda infantil','Infantil','Com linguagem acessível, este guia ajuda as crianças a lidarem com a ansiedade, oferecendo atividades práticas para enfrentar as preocupações do dia a dia.',88,'2006-09-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PreocupaDemais.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PreocupaDemais.jpeg?raw=true');
INSERT INTO "livros" VALUES(153,'Por favor, aprendendo sobre boas maneiras','Beatriz Meirelles','Scipione','Comportamento / Sentimentos','Literatura educativa infantil','Infantil','Este livro divertido apresenta às crianças os conceitos básicos de boas maneiras, como dizer por favor e obrigado, de forma leve e ilustrada.',28,'2014-02-18','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PorFavorBoasManeiras.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PorFavorBoasManeiras.jpeg?raw=true');
INSERT INTO "livros" VALUES(154,'Raiva','Blandina Franco','Pequena Zahar','Comportamento / Sentimentos','Literatura infantil','Infantil','Com ilustrações expressivas e linguagem acessível, o livro fala sobre a emoção da raiva e como lidar com ela no cotidiano infantil.',36,'2019-10-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Raiva.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Raiva.jpeg?raw=true');
INSERT INTO "livros" VALUES(155,'Sentimentos','Núria Roca','Ibep JR','Comportamento / Sentimentos','Literatura educativa infantil','Infantil','Um guia ilustrado que apresenta diferentes sentimentos de forma clara e acessível, ajudando as crianças a reconhecerem e expressarem suas emoções.',30,'2015-04-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Sentimentos.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Sentimentos.jpeg?raw=true');
INSERT INTO "livros" VALUES(156,'Não é brincadeira','Shirley Souza','Escala Integrada','Questões sociais','Literatura infantojuvenil','Infantojuvenil','O livro aborda de maneira sensível temas como bullying e respeito às diferenças, incentivando o diálogo entre crianças e educadores.',40,'2013-08-22','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/NaoBrincadeira.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/NaoBrincadeira.jpeg?raw=true');
INSERT INTO "livros" VALUES(157,'Menina que abraça o vento','Fernanda Paraguassu','Vooinho','Questões sociais','Literatura infantojuvenil','Infantojuvenil','A obra traz uma narrativa sensível sobre identidade, pertencimento e a força do afeto na construção da autoestima.',32,'2017-03-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeninaAbraçaVento.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeninaAbraçaVento.jpeg?raw=true');
INSERT INTO "livros" VALUES(158,'Pequenas confissões','Georgina Martins','Positivo','Questões sociais','Literatura infantojuvenil','Infantojuvenil','Coletânea de histórias curtas e tocantes que revelam sentimentos e desafios do cotidiano infantil, promovendo empatia e reflexão.',40,'2016-09-04','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/PequenasConfissoes.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/PequenasConfissoes.jpeg?raw=true');
INSERT INTO "livros" VALUES(159,'Revolta dos Guarda-Chuvas','Sidónio Muralha','Global','Questões sociais','Literatura infantojuvenil','Infantojuvenil','Uma metáfora poética e crítica sobre resistência, onde os guarda-chuvas ganham vida para protestar contra a opressão e injustiças do cotidiano.',36,'2014-11-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/RevoltaGuardaChuvas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/RevoltaGuardaChuvas.jpeg?raw=true');
INSERT INTO "livros" VALUES(160,'Serafina e a Criança que Trabalha','Jô Azevedo e Iolanda Huzak','Ática','Questões sociais','Literatura educativa','Infantojuvenil','Serafina conhece a realidade de uma criança em situação de trabalho infantil e aprende sobre empatia, justiça e os direitos das crianças.',32,'2017-05-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/SerafinaCriancaTrabalha.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/SerafinaCriancaTrabalha.jpeg?raw=true');
INSERT INTO "livros" VALUES(161,'Refugiadas: Escapando do Céu em Perigo','Maribel Vazquez','Urutau','Questões sociais','Literatura contemporânea','Juvenil','Histórias reais e ficcionais entrelaçadas para mostrar a dura realidade enfrentada por meninas refugiadas em zonas de conflito.',48,'2020-08-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/RefugiadasCéuPerigo.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/RefugiadasCéuPerigo.jpeg?raw=true');
INSERT INTO "livros" VALUES(162,'Se os Tubarões Fossem Homens','Bertolt Brecht','Olhodevidro','Questões sociais','Literatura crítica','Infantojuvenil','Uma fábula provocadora em que tubarões imaginários assumem o lugar de humanos para questionar estruturas de poder e dominação.',24,'2013-06-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/TubaroesHomens.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/TubaroesHomens.jpeg?raw=true');
INSERT INTO "livros" VALUES(163,'Somos Iguais Mesmo Sendo Diferentes','Marcos Ribeiro','Moderna','Questões sociais','Literatura educativa','Infantil','Um convite ao respeito à diversidade e à empatia, mostrando como podemos conviver em harmonia mesmo com diferenças de aparência, cultura e crenças.',32,'2010-03-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/SomosIguaisDiferentes.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/SomosIguaisDiferentes.jpeg?raw=true');
INSERT INTO "livros" VALUES(164,'Boneco do Marcinho','Ermeson Alcalde','Edicon','Questões Sociais','Infantil','Infantil','Marcinho ganha um boneco especial que o ajuda a entender temas como inclusão e respeito às diferenças sociais.',32,'2019-06-05','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/BonecoDoMarcinho.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/BonecoDoMarcinho.jpeg?raw=true');
INSERT INTO "livros" VALUES(165,'Aprendendo a Ser Cidadão','Celia Siqueira','Nobel','Questões Sociais','Infantil','Infantil','Uma introdução ao conceito de cidadania para crianças, com exemplos práticos de direitos e deveres.',36,'2017-09-18','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/AprendendoASerCidadao.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/AprendendoASerCidadao.jpeg?raw=true');
INSERT INTO "livros" VALUES(166,'Aporofobia','Padre Julio Lancellotti','Companhia das Letrinhas','Questões Sociais','Infantil','Infantil','Uma obra sensível que aborda o preconceito contra pessoas em situação de pobreza e incentiva a empatia e o acolhimento.',40,'2022-03-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Aporofobia.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Aporofobia.jpeg?raw=true');
INSERT INTO "livros" VALUES(167,'De Onde Você Veio','Liliana Iacocca e Michele Iacocca','Ática','Questões Sociais','Infantil','Infantil','Uma história lúdica que ajuda as crianças a entenderem suas origens, identidades e histórias de vida.',32,'2016-08-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DeOndeVoceVeio.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DeOndeVoceVeio.jpeg?raw=true');
INSERT INTO "livros" VALUES(168,'Conversando sobre Violência Sexual contra a Criança','Adriana Costa de Miranda','Liber Livro','Questões Sociais','Infantil','Infantojuvenil','Com linguagem acessível, este livro orienta crianças sobre como identificar e denunciar abusos, promovendo a prevenção e proteção.',40,'2018-10-10','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/ConversandoViolenciaSexual.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/ConversandoViolenciaSexual.jpeg?raw=true');
INSERT INTO "livros" VALUES(169,'Da Minha Janela','Otávio Júnior','Companhia das Letrinhas','Questões Sociais','Infantil','Infantil','Um menino observa o mundo a partir de sua janela em uma favela carioca, trazendo reflexões sobre realidade e sonhos.',28,'2020-01-20','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/DaMinhaJanela.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/DaMinhaJanela.jpeg?raw=true');
INSERT INTO "livros" VALUES(170,'Diversidade','Núria Roca','Ibep JR','Questões Sociais','Infantil','Infantil','Um livro que apresenta às crianças a importância de respeitar as diferenças entre as pessoas, promovendo empatia e inclusão.',32,'2014-05-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Diversidade.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Diversidade.jpeg?raw=true');
INSERT INTO "livros" VALUES(171,'Guerra e Paz','Mônica','Martins Editora','Questões Sociais','Infantil','infantil','Com linguagem acessível, o livro aborda o contraste entre a guerra e a paz, incentivando reflexões sobre convivência e empatia.',30,'2017-04-12','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/GuerraEPaz.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/GuerraEPaz.jpeg?raw=true');
INSERT INTO "livros" VALUES(172,'Ester Conhece Paulo Freire','Alê Oliveira','Baioque','Questões Sociais','Infantil','infantil','Ester conhece o educador Paulo Freire e descobre como a educação pode ser um instrumento de transformação social.',36,'2021-09-15','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/EsterConhecePauloFreire.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/EsterConhecePauloFreire.jpeg?raw=true');
INSERT INTO "livros" VALUES(173,'Meu lugar no mundo - Cubatão','Célia de Souza Pinto','Cia da Cultura','Meio ambiente / Reciclagem','Literatura educativa','infantojuvenil','O livro destaca o papel das crianças na preservação do meio ambiente, explorando a cidade de Cubatão como exemplo de transformação ecológica.',28,'2018-06-09','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MeuLugarCubatão.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MeuLugarCubatão.jpeg?raw=true');
INSERT INTO "livros" VALUES(174,'Mário de Andrade – Edição Especial','André Augustus Diasz','Mostarda','Coleção Black Power','Biografia infantil','infantojuvenil','Biografia especialmente adaptada para crianças sobre o modernista Mário de Andrade, destacando sua relação com cultura afro-brasileira e folclore.',28,'2024-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MarioDeAndradeSpecial.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MarioDeAndradeSpecial.jpeg?raw=true');
INSERT INTO "livros" VALUES(175,'Mestre Didi: Deoscóredes M. dos Santos','Marcos Cajé','Mostarda','Coleção Black Power','Biografia infantil ilustrada','infantojuvenil','Retrato de Mestre Didi, artista-sacerdote de Candomblé, mostrando como sua arte e religiosidade influenciaram a cultura afro-brasileira.',32,'2023-01-01','2025-07-30','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/MestreDidi.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/MestreDidi.jpeg?raw=true');
INSERT INTO "livros" VALUES(176,'Conceição Evaristo','Orlando Nilha','Editora Mostarda','Coleção Black Power','Biografia infantil/juvenil','infantojuvenil','Biografia ilustrada da escritora Conceição Evaristo, destacando sua trajetória desde a favela Pendura Saia até se tornar professora, doutora em Literatura e uma das maiores vozes negras do Brasil.',32,'2021-02-08','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Conceicao.jpg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Conceicao.jpeg?raw=true');
INSERT INTO "livros" VALUES(177,'Antônio Francisco Lisboa – Aleijadinho','Fabiano Ormaneze e Douglas Reverie','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Retrata a inspiradora trajetória de Antônio Francisco Lisboa, filho de uma escravizada, que, mesmo acometido pela doença que deformou seu corpo, tornou-se um dos maiores escultores e arquitetos do Brasil colonial.',32,'2021-01-01','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Alejadinho.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Alejadinho.jpeg?raw=true');
INSERT INTO "livros" VALUES(178,'Glória Maria Matta da Silva','Duílio Fabbri Júnior e Manoela Costa','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Biografia ilustrada da jornalista Glória Maria, sua trajetória desde telefonista até se tornar uma das primeiras grandes repórteres negras da TV brasileira, enfrentando ambientes predominantemente masculinos e embranquecidos.',40,'2024-02-15','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Gloria.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Gloria.jpeg?raw=true');
INSERT INTO "livros" VALUES(179,'Abdias do Nascimento','Júlio Emílio Braz','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Conta a história de Abdias do Nascimento, ator, dramaturgo, ativista e fundador do Teatro Experimental do Negro, que lutou pelo reconhecimento dos direitos civis da população negra no Brasil.',32,'2022-01-01','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Abdidas.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Abdidas.jpeg?raw=true');
INSERT INTO "livros" VALUES(180,'Alice Walker','s/a','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Apresenta a vida de Alice Walker, autora de "A Cor Púrpura" e ativista pelos direitos civis e igualdade racial nos EUA.',32,'2021-01-01','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Alice.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Alice.jpeg?raw=true');
INSERT INTO "livros" VALUES(181,'Beatriz Nascimento','Maria Julia Maltese','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Conta a história de Maria Beatriz Nascimento, historiadora sergipana e pioneira no modo de pensar a história a partir de saberes africanos, que estudou os quilombos e foi militante antirracista.',32,'2022-01-01','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Beatriz.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Beatriz.jpeg?raw=true');
INSERT INTO "livros" VALUES(182,'João Cândido','Júlio Emílio Braz','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Conta a história de João Cândido, o "Almirante Negro", líder da Revolta da Chibata em 1910 na Marinha do Brasil.',32,'2023-09-11','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Candido.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Candido.jpeg?raw=true');
INSERT INTO "livros" VALUES(183,'Carolina Maria de Jesus','Orlando Nilha','Mostarda','Coleção Black Power','Biografia infantojuvenil','infantojuvenil','Trajetória de Carolina Maria de Jesus, catadora de papel e autora de Quarto de Despejo, que se tornou uma das mais importantes escritoras negras do Brasil.',32,'2019-01-01','2025-07-10','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/capa/Carolina.jpeg?raw=true','https://github.com/Arthurbhs/Biblioteca_APP/blob/main/assets/images/thumbnaill/Carolina.jpeg?raw=true');
